#define TO_SHORT_COLOR(n) (n * 256)

static uint8_t lcd_active = 1;

void st7565_on_user(void) {
    lcd_active = 1;
}

void st7565_off_user(void) {
    ergodox_infinity_lcd_color(0, 0, 0);
    lcd_active = 0;
}


void st7565_task_user(void) {
    if (!lcd_active) {
        return;
    }

    st7565_set_cursor(0, 0);

    if (is_keyboard_master()) {
        st7565_write_raw_P(animation_bits[animation_current_frame], 128 * 32);
    } else {
        if (IS_LAYER_ON(MDIA)) {
            st7565_write_raw_P(media_bits, 128 * 32);
        } else if (IS_LAYER_ON(SYMB)) {
            st7565_write_raw_P(symbol_bits, 128 * 32);
        } else if (IS_LAYER_ON(BASE)) {
            st7565_write_raw_P(querty_bits, 128 * 32);
        }
    }

    // Set LCD colors based on layer in both halves
    if (IS_LAYER_ON(MDIA)) {
        ergodox_infinity_lcd_color(0, 0, TO_SHORT_COLOR(255));
    } else if (IS_LAYER_ON(SYMB)) {
        ergodox_infinity_lcd_color(0, TO_SHORT_COLOR(128), 0);
    } else if (IS_LAYER_ON(BASE)) {
        ergodox_infinity_lcd_color(0, TO_SHORT_COLOR(128), TO_SHORT_COLOR(128));
    }
}

